import React from 'react';

const AddedTask=({completed})=>{

    const containerStyle={
        height:'15%',
        width:'100%',
        backgroundColor: "#e0e0de",
        borderRadius: 50,
        margin: 50
    }

    const fillerStyles = {
        height: '100%',
        width: `${completed}%`,
        backgroundColor: 'rgb(239, 68, 11)',
        borderRadius: 'inherit',
        textAlign: 'right',
        transition: 'width 1s ease-in-out',
      }

      const labelStyles = {
        padding: 5,
        color: 'white',
        fontWeight: 'bold'
      }
    return(
        <div className='taskMainContainer' style={containerStyles}>
            <div className='contentContainer' style={fillerStyles}>
                <span style={labelStyles}>{`${completed}%`}</span>
            </div>
        </div>
    )
}

export default AddedTask;
