import React,{useState} from 'react';
import './TotalTargetTime.scss' 
const TotalTargetTime=()=>{

    let timeArr=[]
    const handleChange=event=>{
       if(timeArr.length<6){
            //timeArr.push(event.target.value)
            timeArr[parseInt(event.target.name)]=event.target.value
            if(parseInt(event.target.name)<5){
                document.getElementById(parseInt(event.target.name)+1).focus();
            }

       }
       else{
           timeArr.pop();
           timeArr.push(event.target.value)
       }


    }
    return(
        <div className='timcardsContainer'>
            <input className='timeBox' name='0' id='0' value={timeArr[0]} maxlength='1' onChange={handleChange}>
            </input>
            <input className='timeBox' name='1' id='1' value={timeArr[1]} maxlength='1' onChange={handleChange}>
            </input>
            <div className='timerText'>h</div>
            <input className='timeBox' name='2' id='2' value={timeArr[2]} maxlength='1' onChange={handleChange}>
            </input>
            <input className='timeBox' name='3' id='3' value={timeArr[3]} maxlength='1' onChange={handleChange}>
            </input>
                <div className='timerText'>m</div>
                <input className='timeBox' name='4' id='4' value={timeArr[4]} maxlength='1' onChange={handleChange}>
            </input>
            <input className='timeBox'  name='5' id='5' onfocus='this.value=""' value={timeArr[5]} maxlength='1' onChange={handleChange}>
            </input>
                <div className='timerText'>s</div>

        </div>

    )
}

export default TotalTargetTime;
