import React from 'react';
import {useSelector} from 'react-redux';
import {Doughnut} from 'react-chartjs-2';

const DoughnutChart=()=>{
    const {key,data}=useSelector(state=>state.SubmitDataReducer);
    let chartLabels=[];
    let taskHour=[];
    console.log()
    if(Object.keys(data[key]).lenght0){
    let obj=data[key].taskData;
    console.log(obj)
    for(var k in obj){
        chartLabels.push(k);

        taskHour.push(((obj[k].time)/3600).toFixed(2))

        }
    }

    const Doughnutdata = {
        labels: chartLabels,
        datasets: [
          {
            label: '# of Votes',
            data: taskHour,
            backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)',
            ],
            borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)',
            ],
            borderWidth: 5,
          },
        ],
      };
    return(
        <div className='doughnutChartContainer'>
            {Object.keys(data[key]).lenght>0?
            <Doughnut data={Doughnutdata}/>
            :''}
        </div>
    )
}

export default DoughnutChart;
